import React from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/sass/styles.scss';
 
const localizer = momentLocalizer(moment)
 
console.log('got to calendar component');
class CalendarTest extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			events : [],
			eventImg : '',
			eventLink : ''
		}}

	getEvents= () => {
		const fetchUrl = 'http://localhost:5000/calendar/' + this.props.countryCode + '/' + this.props.city;
		fetch(fetchUrl)
		.then(res => res.json())
		.then(data => {
			const eventsArr = data[Object.keys(data)[0]].events;
			console.log('arr:', eventsArr.events);
			const tempEventsArr = eventsArr.map(event => {
				console.log('event is:', event);
				const startDate = new Date(event.dates.start.dateTime);
				const endDate = new Date(startDate + 1);
				const obj = {
					id : event.id,
					title : event.name,
					start : startDate,
					end : endDate,
					img : event.images[5].url,
					ticketsUrl : event.url
				}
				console.log('obj is:', obj);
				return obj;
			})
			this.setState({events : tempEventsArr}, this.addClickFunctions);
		});
	}

	SaveSearch = () => {
		fetch('http://localhost:5000/saveSearch', {
	      method: 'post',
	      headers: {'Content-Type': 'application/json'},
	      body: JSON.stringify({
	        user : this.props.userId,
	        city: this.props.city,
	        countryCode: this.props.countryCode
	      })
	    })
	      .then(response => response.json())
	      .then(user => {
	      	console.log('user back in cal:',user);
	      	console.log('userid back in cal:',user[0]);
	        if (user[0] === parseInt(this.props.userId)) {
	        	const btnDiv = document.getElementById('buttonDiv');
	        	const msg = document.createElement("p");
	        	msg.textContent = "search saved to your profile!";
	        	btnDiv.appendChild(msg);
	        }
	      })
	}
	addClickFunctions = () => {
		const eventsClasses = document.querySelectorAll(".rbc-event");
		if(eventsClasses){
			eventsClasses.forEach(div => {
			div.addEventListener("click", () => {this.onEventClick(div)});
		})
		}
	}

	onEventClick = (div) => {
		console.log(div);
		const eventDataDiv = document.createElement('div');
		eventDataDiv.className = 'singleEvent';
		const eventTitle = document.createElement('h2');
		const eventImg = document.createElement('img');
		const eventLink = document.createElement('a');
		eventTitle.textContent = div.firstElementChild.title;
		eventImg.src={eventImg};
		eventLink.href = {eventLink};
		eventDataDiv.appendChild(eventTitle);
		eventDataDiv.appendChild(eventImg);
		eventDataDiv.appendChild(eventLink);
		div.appendChild(eventDataDiv);
	}
	componentDidMount() {
		if(this.props.city){
			this.getEvents();
		}
  	}

	render(){
		const {events, eventImg, eventLink} = this.state;
		if(this.props.isSignedIn){
			return(
				<div style={{display:'flex', justifyContent:'center'}}>
					<div>
					    <Calendar
					      localizer={localizer}
					      events={events}
					      startAccessor="start"
					      endAccessor="end"
					      style={{height: 500}}
					    />
				  	</div>
				  	<div className='hidden'>
				  		<h2>title</h2>
				  		<img alt='event' src={eventImg} style={{width:'300px'}}/>
				  		<a href={eventLink}>Order Tickets Now!</a>
				  	</div>
					<div id='buttonDiv'>
					<button className='h3 ma4' onClick={this.SaveSearch}>Save This Search</button>
					</div>
				</div> 
			);
		}else{
			return(
				<div id='calendar' className='pa4' style={{display:'flex', justifyContent:'center'}}>
					<div className='pa3 ma2 shadow-5'>
					    <Calendar
					      localizer={localizer}
					      events={events}
					      startAccessor="start"
					      endAccessor="end"
					      style={{height: 500}}
					    />
				  	</div>
				</div>
			);
		}
	}
		
}

export default CalendarTest;
