import React from 'react';
import Calendar from './Calendar';

class Profile extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			cal : '',
			city : '',
			countryCode : '',
			isSignedIn : true
		}
	}

	getSavedSearches = () => {
		const fetchUrl = 'http://localhost:5000/userSearch/' + this.props.userId;
		fetch(fetchUrl)
	    .then(res => res.json())
	    .then(data => {
	    	data.forEach(obj => {
	    		const city = obj.city;
	    		const countryCode = obj.countrycode;
	    		const mainDiv = document.getElementById('main');
	    		const newCal = document.createElement('div');
	    		newCal.className = 'grow pa3 ba br3 b--blue bg-lightest-blue ma2 pointer';
	    		newCal.textContent = city;
	    		newCal.value = city;
	    		newCal.addEventListener("click", () => {
	    			this.setState({city : city, countryCode : countryCode}, this.createCalendar(newCal.value))
	    		});
	    		mainDiv.appendChild(newCal);
	    	})
	      console.log(data);
	    });
	}

	createCalendar = (val) => {
		this.setState({ cal : val});
	}

	getStarredEvents = () => {

	}
	
	componentDidMount(){
		this.getSavedSearches();
	}

	render(){
		const {cal, city, countryCode} = this.state;
		if (cal){
			console.log('props in render:' ,cal, city, countryCode);
			return(
				<Calendar city={city} countryCode={countryCode} isSignedIn={this.state.isSignedIn} userId={this.props.userId}/>
			);
		}else {
			return(
			<div id='main'>
				<h1>Your Saved Searchs:</h1>
			</div>
			);
		}
		
	}
	
}

export default Profile;
