import React from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/sass/styles.scss';
import './Calendar.css';
import EventDetails from './EventDetails';
 
const localizer = momentLocalizer(moment)
 
console.log('got to calendar component');
class CalendarTest extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			events : [],
			eventTitle : 'Powered By',
			eventImg : 'https://uk.tmconst.com/ccp-salesforce-images/DK/ticketmaster_guider_720x405.jpg',
			eventLink : 'https://www.ticketmaster.com/'
		}}

	getEvents= () => {
		const fetchUrl = 'http://localhost:5000/calendar/' + this.props.countryCode + '/' + this.props.city;
		fetch(fetchUrl)
		.then(res => res.json())
		.then(data => {
			const eventsArr = data[Object.keys(data)[0]].events;
			console.log('arr:', eventsArr.events);
			const tempEventsArr = eventsArr.map(event => {
				console.log('event is:', event);
				const startDate = new Date(event.dates.start.dateTime);
				const endDate = new Date(startDate + 1);
				const obj = {
					id : event.id,
					title : event.name,
					start : startDate,
					end : endDate,
					img : event.images[5].url,
					ticketsUrl : event.url,
					showEvent: false
				}
				console.log('obj is:', obj);
				return obj;
			})
			this.setState({events : tempEventsArr}, this.addClickFunctions);
		});
	}

	SaveSearch = () => {
		fetch('http://localhost:5000/saveSearch', {
	      method: 'post',
	      headers: {'Content-Type': 'application/json'},
	      body: JSON.stringify({
	        user : this.props.userId,
	        city: this.props.city,
	        countryCode: this.props.countryCode
	      })
	    })
	      .then(response => response.json())
	      .then(user => {
	      	console.log('user back in cal:',user);
	      	console.log('userid back in cal:',user[0]);
	        if (user[0] === parseInt(this.props.userId)) {
	        	const btnDiv = document.getElementById('buttonDiv');
	        	const msg = document.createElement("p");
	        	msg.textContent = "search saved to your profile!";
	        	btnDiv.appendChild(msg);
	        }
	      })
	}
	addClickFunctions = () => {
		const eventsClasses = document.querySelectorAll(".rbc-event");
		if(eventsClasses){
			console.log('found eventClasses of rbc-event');
			eventsClasses.forEach(div => {
				const selectedEvent = this.getEventObj(div);
				console.log('selected events in addclicks:', selectedEvent);
				console.log('div to add listener:', div);
				div.addEventListener("mouseover", () => {
					console.log('recognized mouseover, div:', div);
					this.setState({eventImg : selectedEvent[0].img, 
								eventLink:selectedEvent[0].ticketsUrl, 
								eventTitle:selectedEvent[0].title}, this.setState({showEvent : true}));
				});
		})
		}
	}

	getEventObj = (div) => {
		console.log(div);
		let title;
		if(div.title){
			title = div.children[1].innerText;
		}else {
			title=div.firstElementChild.title;
		}
		console.log('title:', title);
		const selectedEvent = (this.state.events).filter(obj => obj.title === title);
		console.log('selected events:', selectedEvent);
		return selectedEvent;
		
	}
	
	componentDidMount() {
		if(this.props.city){
			this.setState({showEvent : false}, this.getEvents);
		}
		const toolBar = document.getElementsByClassName('rbc-toolbar')[0];
		toolBar.addEventListener("click", () => { console.log('adding click on toolbar'); this.addClickFunctions();});
  	}

	render(){
		const {events, eventImg, eventLink, eventTitle, showEvent} = this.state;
		if(this.props.isSignedIn){
			return(
				<div style={{display:'flex', justifyContent:'center'}}>
					<div>
					    <Calendar
					      localizer={localizer}
					      events={events}
					      startAccessor="start"
					      endAccessor="end"
					      style={{height: 500}}
					    />
				  		<EventDetails eventImg={eventImg} eventLink={eventLink} eventTitle={eventTitle} showEvent={showEvent}/>
				  		<div id='buttonDiv'>
							<button className='h3 ma4' onClick={this.SaveSearch}>Save This Event</button>
						</div>
				  	</div>
				</div> 
			);
		}else{
			return(
				<div id='calendar' className='pa4'>
					<div className='pa3 ma2 shadow-5' style={{display:'flex', justifyContent:'center'}}>
					    <Calendar
					      localizer={localizer}
					      events={events}
					      startAccessor="start"
					      endAccessor="end"
					      style={{height: 500}}
					    />
					    <EventDetails eventImg={eventImg} eventLink={eventLink} eventTitle={eventTitle} showEvent={showEvent}/>
				  	</div>
				</div>
			);
		}
	}
		
}

export default CalendarTest;
