import React from 'react';
import EventCalendar from 'react-event-calendar';

// const events = [
//     {
//         start: '2015-07-20',
//         end: '2015-07-02',
//         eventClasses: 'optionalEvent'
//         title: 'test event',
//         description: 'This is a test description of an event',
//     },
//     {
//         start: '2015-07-19',
//         end: '2015-07-25',
//         title: 'test event',
//         description: 'This is a test description of an event',
//         data: 'you can add what ever random data you may want to use later',
//     },
// ];

console.log('got to calendar component');
class CalendarTest extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			events : []
		}
	}

	// createCalendar = () =>{
	// 	const city = this.props.city;
	// 	const countryCode = this.props.countryCode;
	// 	const calDiv = document.getElementById('calendar');
	// 	if (calDiv.childElementCount){
	//     	let innerDiv = document.getElementById('innerCalendar');
	//     	innerDiv.remove();
	//     }
	// 	const div = document.createElement('div');
		
	// 	calDiv.appendChild(div);
	// }

	getEvents= () => {
		const fetchUrl = 'http://localhost:5000/calendar/' + this.props.countryCode + '/' + this.props.city;
		fetch(fetchUrl).then(res => res.json()).then(data => this.setState({events : data}));
	}

	SaveSearch = () => {
		fetch('http://localhost:5000/saveSearch', {
	      method: 'post',
	      headers: {'Content-Type': 'application/json'},
	      body: JSON.stringify({
	        user : this.props.userId,
	        city: this.props.city,
	        countryCode: this.props.countryCode
	      })
	    })
	      .then(response => response.json())
	      .then(user => {
	      	console.log('user back in cal:',user);
	      	console.log('userid back in cal:',user[0]);
	        if (user[0] === parseInt(this.props.userId)) {
	        	const btnDiv = document.getElementById('buttonDiv');
	        	const msg = document.createElement("p");
	        	msg.textContent = "search saved to your profile!";
	        	btnDiv.appendChild(msg);
	        }
	      })
	}

	componentDidMount() {
		if(this.props.city){
			this.getEvents();
		}
  	}

	render(){
		const {events} = this.state;
		if(this.props.isSignedIn){
			return(
				<div style={{display:'flex', justifyContent:'center'}}>
					<div>
						<EventCalendar 
						    month={1}
						    year={2019}
						    events={events} 
						    onEventClick={(target, eventData, day) => console.log(eventData)}/>
					</div>
					<div id='buttonDiv'>
					<button className='h3 ma4' onClick={this.SaveSearch}>Save This Search</button>
					</div>
				</div> 
			);
		}else{
			return(
				<div id='calendar' className='pa4' style={{display:'flex', justifyContent:'center'}}>
					<EventCalendar 
						    month={1}
						    year={2019}
						    events={events} 
						    onEventClick={(target, eventData, day) => console.log(eventData)}/>
				</div>
			);
		}
	}
		
}

export default Calendar;
