import React from 'react';

console.log('got to CityDropdown file');
class CityDropdown extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			cityList : []
		}
	}

	updateOptions = () => {
		const cities = this.state.cityList;
	    console.log('cities in dropdown:', cities);
	    const select = document.getElementById('cities');
	    cities.forEach(city => {
	    	let op = document.createElement('option');
	    	op.textContent = city;
	    	op.value = city;
	    	select.appendChild(op);
	    })
	}
	
	componentDidMount() {
		this.setState({cityList : this.props.citiesArr}, this.updateOptions);
  	}

	render(){
		return (
			<div>
				<select id='cities'>
				</select>
			</div>
		);
	}
}

export default CityDropdown;
