import React from 'react';
import './Header.css'; 


const Header = ({onRouteChange, isSignedIn}) => {
	return (
		<header >
			<div className='background-image-container'>
			<div style={{ position: 'absolute', left:'45%',top:'10%'}}>
			<div style={{position:'relative',left:'-45%'}}>
				<h1> Where are you traveling to?</h1>
				<div className='br3 pa5 shadow-5' style={{display:'flex', justifyContent:'center', backgroundColor:'#24478f'}}>
					<CountryDropdown
					className='pa3 ba br3 b--blue bg-lightest-blue ma2'
			          value={country}
			          onChange={(val) => this.onCountryChange(val)}/>
					<div id='cities'>
						<select id='citySelector' className='pa3 ba br3 b--blue bg-lightest-blue ma2' onChange={(val) => this.onCityChange(val)}>
							<option value>Select City</option>
						</select>
					</div>
				</div>
			</div>
			</div>
				<div className="placeholder">
				</div>
			</div>			
	    </header>
	);
}

export default Header;
