import React from 'react';


class CityDropdown extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			cityList : []
		}
	}

	getCitiesOfCountry = () => {
		fetch('https://localhost:5000/cities', {
	      method: 'post',
	      headers: {'Content-Type': 'application/json'},
	      body: JSON.stringify({
	        code: this.props.countryCode
	      })
	    })
	    .then(res => res.json())
	    .then(data => {
	    	console.log(data);
	    })
	}

	componentDidMount() {
	    this.getCitiesOfCountry();
  	}

	render(){
		return (
			<div>
				<select>
				</select>
			</div>
		);
	}
}

export default CityDropdown;
