import React from 'react';
import SignIn from './components/SignIn';
import Register from './components/Register';
import Header from './components/Header';
import SearchBar from './components/SearchBar';
import Nav from './components/Nav';
import Profile from './components/Profile';
import './App.css';

class App extends React.Component{
  constructor(){
    super()
    this.state = {  
      route : '/home',
      isSignedIn : false,
      userId : ''
    }
  }

  onRouteChange = (route) => {
      if(route === '/signout'){
        this.setState({isSignedIn: false}, this.setState({route: '/signin'}));
      } else{
        this.setState({route: route});
      }
  }

  componentDidMount(){
    const myUrl = window.location.href; 
    if (myUrl.includes("profile")){
      const urlSeg = myUrl.split('/');
      const id = urlSeg[urlSeg.length-1];
      this.setState({isSignedIn : true, userId : id}, this.setState({route: '/home'}));
    }
  }
  render () {
    const {route, isSignedIn, userId  } = this.state;
    return (
      <div className="App">
        <Nav onRouteChange={this.onRouteChange} isSignedIn={isSignedIn}/>
        {route ==='/home'
          ? <div>
          <Header onRouteChange={this.onRouteChange} isSignedIn={isSignedIn}/>
          <SearchBar isSignedIn={isSignedIn} userId={userId}/>
          </div>
          : (route === '/signin'
              ? <SignIn onRouteChange={this.onRouteChange}/>
              : ( route === '/register'
                ? <Register onRouteChange={this.onRouteChange}/>
                : ( route === '/profile'
                    ? <Profile userId={this.state.userId}/>
                    : <div>No route found</div>
                  )
                )
            )
        }
      </div>
    );
  }
}

export default App;
