import React from 'react';
import SignIn from './components/SignIn';
import Register from './components/Register';
import Header from './components/Header';
import SearchBar from './components/SearchBar';
import Profile from './components/Profile';
import './App.css';

class App extends React.Component{
  constructor(){
    super()
    this.state = {  
      route : '/home',
      isSignedIn : false,
      userId : ''
    }
  }

  onRouteChange = (route) => {
      this.setState({route: route});
  }

  componentDidMount(){
    const myUrl = window.location.href; 
    if (myUrl.includes("profile")){
      const urlSeg = myUrl.split('/');
      const id = urlSeg[urlSeg-1];
      this.setState({isSignedIn : true, uderId : id}, this.setState({route: '/home'}));
    }
  }
  render () {
    const {route} = this.state;
    return (
      <div className="App">
        <Header onRouteChange={this.onRouteChange} isSignedIn={this.state.isSignedIn}/>
        {route ==='/home'
          ? <SearchBar isSignedIn={this.state.isSignedIn} userId={this.state.userId}/>
          : (route === '/signIn'
              ? <SignIn onRouteChange={this.onRouteChange}/>
              : ( route === '/register'
                ? <Register onRouteChange={this.onRouteChange}/>
                : ( route === '/profile'
                    ? <Profile />
                    : <div>No route found</div>
                  )
                )
            )
        }
      </div>
    );
  }
}

export default App;
