import React from 'react';
import Calendar from './Calendar';
import { CountryDropdown } from 'react-country-region-selector';
import { getCode } from 'country-list';

class SearchBar extends React.Component {
	constructor(props){
		super(props)
		this.state = {
			country : '',
			countryCode : '',
			city : '',
			cityList : [],
			period : '',
			calChanged : false
		}
	}

	onCountryChange (val) {
	    this.setState({ country: val },this.getCountryCode);
	}

	getCountryCode = () => {
		console.log('getCountryCode');
		const countryName = this.state.country;
		if(countryName === 'United States'){
			this.setState({countryCode : 'US'}, this.getCitiesOfCountry);
		}else{
			console.log('name:',countryName);
			console.log('code:', getCode(countryName));
			this.setState({countryCode : getCode(countryName)}, this.getCitiesOfCountry);
		}
	}

	onCityChange (val) {
		if(this.state.calChanged === true){
			this.setState({calChanged : false}, this.setState({ city: val }));
		}
		this.setState({calChanged : true});
	}

	

	getCitiesOfCountry = () => {
		console.log('code in get:', this.state.countryCode);
		fetch('http://localhost:5000/cities', {
	      method: 'post',
	      headers: {'Content-Type': 'application/json'},
	      body: JSON.stringify({
	        code: this.state.countryCode
	      })
	    })
	    .then(res => res.json())
	    .then(data => {
	    	this.setState({cityList : data}, this.updateCityOptions);
	    })
	}

	updateCityOptions = () => {
		const cities = this.state.cityList;
	    console.log('cities in dropdown:', cities);
	    const div = document.getElementById('cities');
	    if (div.childElementCount){
	    	let select = document.getElementById('citySelector');
	    	select.remove();
	    }
    	const select = document.createElement('select');
    	select.id = 'citySelector';
    	select.className = 'pa3 ba br3 b--blue bg-lightest-blue ma2';
    	select.addEventListener("change", () => {this.onCityChange(select.value)});
	    div.appendChild(select);
	    cities.forEach(city => {
	    	let op = document.createElement('option');
	    	op.textContent = city;
	    	op.value = city;
	    	select.appendChild(op);
	    	}, this.setState({city : cities[0]}))  
	}

	componentDidMount() {
		console.log('componentDidMount');
		this.forceUpdate();
	    
  	}

  	render(){
  		const {country, countryCode, city, calChanged} = this.state;
  		console.log('got to render, city is:', city);
		return (
			<div>
				<div className='pa5 shadow-5 bg-washed-blue' style={{display:'flex', justifyContent:'center'}}>
					<CountryDropdown
					className='pa3 ba br3 b--blue bg-lightest-blue ma2'
			          value={country}
			          onChange={(val) => this.onCountryChange(val)}/>
					<div id='cities'>
						<select id='citySelector' className='pa3 ba br3 b--blue bg-lightest-blue ma2' onChange={(val) => this.onCityChange(val)}>
							<option value>Select City</option>
						</select>
					</div>
				</div>

				
					<Calendar city={city} countryCode={countryCode}/> : <div></div>
				
				
				
		    </div>
		);
	}
}

export default SearchBar;
