import React from 'react';
import cities from 'all-the-cities';

class CityDropdown extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			cityList : []
		}
	}

	getCitiesOfCountry = () => {
		const allCities = cities.filter(city => {
			return city.country.match(this.props.countryCode);
		})
		this.setState({cityList : allCities});
		console.log('cities array: ',allCities);
	}

	componentDidMount() {
	    this.getCitiesOfCountry();
  	}

