import React from 'react';
import { CountryDropdown } from 'react-country-region-selector';
import { getCode } from 'country-list';
import CityDropdown from './CityDropdown';

class SearchBar extends React.Component {
	constructor(props){
		super(props)
		this.state = {
			country : '',
			countryCode : '',
			city : '',
			period : ''
		}
	}

	selectCountry (val) {
		console.log('selectCountry');
	    this.setState({ country: val });
	}

	getCountryCode = () => {
		console.log('getCountryCode');
		const countryName = this.state.country;
		console.log('name:',countryName);
		console.log('code:', getCode(countryName));
		this.setState({countryCode : getCode(countryName)});
	}

	selectCity (val) {
	    this.setState({ country: val });
	}

	
	componentDidMount() {
		console.log('componentDidMount');
	    const script = document.createElement("script");
	    script.async = true;
	    script.src = "https://ticketmaster-api-staging.github.io/products-and-docs/widgets/calendar/1.0.0/lib/main-widget.js";
	    this.div.appendChild(script);
	    this.getCountryCode();
  	}
  	render(){
  		const {country, city, countryCode} = this.state;
  		console.log('render', countryCode);
		return (
			<div>
				<div style={{display:'flex'}}>
					<CountryDropdown
			          value={country}
			          onChange={(val) => this.selectCountry(val)}/>
					<CityDropdown
						countryCode={countryCode}
			          value={city}
			          onChange={(val) => this.selectCity(val)}/>
				</div>
				<div ref={el => (this.div = el)}>
					<div w-type="calendar" w-tmapikey="BTAzLLcXX3xXtooi8WdIktbOn1C3irib" w-googleapikey="YOUR_GOOGLE_API_KEY" w-keyword="" w-theme="calendar" w-colorscheme="light" w-width="298" w-height="400" w-size="25" w-border="1" w-borderradius="4" w-postalcode="" w-radius="25" w-countrycode="US" w-city="Los Angeles" w-period="Thu Jan 09 2020 13:04:11 GMT+0200 (Israel Standard Time)" w-periodweek="week" w-layout="vertical" w-classificationid="" w-attractionid="" w-promoterid="" w-venueid="" w-affiliateid="" w-segmentid="" w-proportion="standart" w-latlong=""></div>
			    </div>
		    </div>
		);
	}
}

export default SearchBar;
