import React from 'react';
import './Calendar.css';
 
class EventDetails extends React.Component{
	constructor(props){
		super(props)
		this.state={

		}
	}

	render(){
		const {eventImg, eventLink, eventTitle, showEvent}=this.props;
		if(showEvent){
			return(
			<div id='details' style={{height:'100%'}} className='tc dib br3 pa4 ma3 mb5 dib bw2 shadow-5'>
				<div id='eventDetails'>
					<h2>{eventTitle}</h2>
					<img src={eventImg} style={{width:'350px'}}/>
					<a href={eventLink} className='ma2 dib'>Order Tickets Now!</a>
				</div>
			</div>
			)
		}else{
			return(
			<div id='details' style={{height:'100%'}} className='tc dib br3 pa4 ma3 mb5 dib bw2 shadow-5'>
				<div id='eventDetails'>
					<h2>Powered By</h2>
					<img src='https://uk.tmconst.com/ccp-salesforce-images/DK/ticketmaster_guider_720x405.jpg' style={{width:'350px'}}/>
					<a href='https://www.ticketmaster.com/' className='ma2 dib'>Order Tickets Now!</a>
				</div>
			</div>
			)
		}
		
	}
}

export default EventDetails;
