import React from 'react';
import SignIn from './components/SignIn';
import Register from './components/Register';
import Header from './components/Header';
import SearchBar from './components/SearchBar';
import Nav from './components/Nav';
import CalendarTest from './components/CalendarTest';
import Recomendations from './components/Recomendations';
import Footer from './components/Footer'
import Profile from './components/Profile';
import './App.css';

class App extends React.Component{
  constructor(){
    super()
    this.state = {  
      route : '/home',
      isSignedIn : false,
      userId : '',
      calChanged : false,
      city : '',
      countryCode : ''
    }
  }

  onRouteChange = (route) => {
      if(route === '/signout'){
        this.setState({isSignedIn: false}, this.setState({route: '/signin'}));
      } else{
        this.setState({route: route});
      }
  }

  onCityChange = (val) => {
    if(this.state.calChanged === true){
      this.setState({calChanged : false}, this.setState({ city: val }));
    }
    this.setState({calChanged : true});
  }

  onCountryChange = (val) => {
    this.setState({countryCode : val});
  }

  componentDidMount(){
    const myUrl = window.location.href; 
    if (myUrl.includes("profile")){
      const urlSeg = myUrl.split('/');
      const id = urlSeg[urlSeg.length-1];
      this.setState({isSignedIn : true, userId : id}, this.setState({route: '/home'}));
    }
  }
  render () {
    const {route, isSignedIn, userId, city, countryCode, calChanged } = this.state;
    return (
      <div className="App">
        <Nav onRouteChange={this.onRouteChange} isSignedIn={isSignedIn}/>
        {route ==='/home'
          ? <div>
          <Header onRouteChange={this.onRouteChange} isSignedIn={isSignedIn} userId={userId} onCityChange={this.onCityChange} onCountryChange={this.onCountryChange}/>
          {
            calChanged
            ?<CalendarTest city={city} countryCode={countryCode} isSignedIn={isSignedIn} userId={userId}/> 
            :<Recomendations /> 
          }
          </div>
          : (route === '/signin'
              ? <SignIn onRouteChange={this.onRouteChange}/>
              : ( route === '/register'
                ? <Register onRouteChange={this.onRouteChange}/>
                : ( route === '/profile'
                    ? <Profile userId={this.state.userId}/>
                    : <div>No route found</div>
                  )
                )
            )
        }
        <Footer />
      </div>
    );
  }
}

export default App;
