import React from 'react';
import { CountryDropdown } from 'react-country-region-selector';
import { getCode } from 'country-list';
import CityDropdown from './CityDropdown';

class SearchBar extends React.Component {
	constructor(props){
		super(props)
		this.state = {
			country : '',
			countryCode : '',
			city : '',
			cityList : [],
			period : '',
			calendarAdded : false
		}
	}

	selectCountry (val) {
		console.log('selectCountry');
	    this.setState({ country: val },this.getCountryCode);
	}

	getCountryCode = () => {
		console.log('getCountryCode');
		const countryName = this.state.country;
		console.log('name:',countryName);
		console.log('code:', getCode(countryName));
		this.setState({countryCode : getCode(countryName)}, this.getCitiesOfCountry);
	}

	selectCity (val) {
	    this.setState({ city: val }, this.createCalendar);
	    console.log('in select city, val:', val);

		// fetch('http://localhost:5000/calendar', {
	 //      method: 'post',
	 //      headers: {'Content-Type': 'application/json'},
	 //      body: JSON.stringify({
	 //        code: this.state.countryCode,
	 //        city : val
	 //      })
	 //    })
	 //    .then(res => res.json())
	 //    .then(data => {
	 //    	console.log(data);
	 //    })
	}

	createCalendar = () =>{
		const city = this.state.city;
		const countryCode = this.state.countryCode;
		const calDiv = document.getElementById('calendar');
		const div = document.createElement('div');
		div.id='innerCalendar';
		div.setAttribute('w-type', 'calendar');
		calDiv.appendChild(div);
		this.setState({calendarAdded : true});

	}

	getCitiesOfCountry = () => {
		console.log('code in get:', this.state.countryCode);
		fetch('http://localhost:5000/cities', {
	      method: 'post',
	      headers: {'Content-Type': 'application/json'},
	      body: JSON.stringify({
	        code: this.state.countryCode
	      })
	    })
	    .then(res => res.json())
	    .then(data => {
	    	this.setState({cityList : data}, this.updateOptions);
	    })
	}

	updateOptions = () => {
		const cities = this.state.cityList;
	    console.log('cities in dropdown:', cities);
	    const div = document.getElementById('cities');
	    if (div.childElementCount){
	    	let select = document.getElementById('citySelector');
	    	select.remove();
	    }
    	const select = document.createElement('select');
    	select.id = 'citySelector';
    	select.addEventListener("change", () => {this.selectCity(select.value)});
	    div.appendChild(select);
	    cities.forEach(city => {
	    	let op = document.createElement('option');
	    	op.textContent = city;
	    	op.value = city;
	    	select.appendChild(op);
	    	}, this.setState({city : cities[0]}))  
	}

	componentDidMount() {
		console.log('componentDidMount');
	    const script = document.createElement("script");
	    script.async = true;
	    script.src = "https://ticketmaster-api-staging.github.io/products-and-docs/widgets/calendar/1.0.0/lib/main-widget.js";
	    const calDiv = document.getElementById('calendar');
	    calDiv.appendChild(script);
  	}

  	render(){
  		const {country, city, countryCode} = this.state;
  		console.log('render', countryCode);
		return (
			<div>
				<div style={{display:'flex'}}>
					<CountryDropdown
			          value={country}
			          onChange={(val) => this.selectCountry(val)}/>
					<div id='cities'>
					</div>
				</div>
				<div id='calendar'>
			    </div>
		    </div>
		);
	}
}

export default SearchBar;
