import React from 'react';
import SignIn from './components/SignIn';
import Header from './components/Header';
import SearchBar from './components/SearchBar';
import './App.css';

class App extends React.Component{
  constructor(){
    super()
    this.state = {  
      route : '/home'
    }
  }

  onRouteChange = (route) => {
      this.setState({route: route});
  }
  render () {
    return (
      <div className="App">
        <Header />
        {route ==='/home'
          ? <SearchBar />
          : (route === '/signIn'
              ? <SignIn />
              : <SearchBar />
            )
        }
      </div>
    );
  }
}

export default App;
