import React from 'react';


class CityDropdown extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			cityList : this.props.citiesArr
		}
	}

	componentDidMount() {
	    const cities = this.state.cityList;
	    const select = document.getElementById('cities');
	    cities.forEach(city => {
	    	let op = document.createElement('option');
	    	op.textContent = city;
	    	op.value = city;
	    	select.appendChild(op);
	    })
  	}

	render(){
		return (
			<div>
				<select id='cities'>
				</select>
			</div>
		);
	}
}

export default CityDropdown;
