import React from 'react';

console.log('got to CityDropdown file');
class CityDropdown extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			cityList : []
		}
	}

	getCitiesOfCountry = () => {
		console.log('code in get:', this.state.countryCode);
		fetch('http://localhost:5000/cities', {
	      method: 'post',
	      headers: {'Content-Type': 'application/json'},
	      body: JSON.stringify({
	        code: this.state.countryCode
	      })
	    })
	    .then(res => res.json())
	    .then(data => {
	    	this.setState({cityList : data}, this.updateOptions);
	    })
	}

	updateOptions = () => {
		const cities = this.state.cityList;
	    console.log('cities in dropdown:', cities);
	    const select = document.getElementById('cities');
	    cities.forEach(city => {
	    	let op = document.createElement('option');
	    	op.textContent = city;
	    	op.value = city;
	    	select.appendChild(op);
	    })
	}

	render(){
		return (
			<div>
				<select id='cities'>
				</select>
			</div>
		);
	}
}

export default CityDropdown;
