import React from 'react';

console.log('got to calendar component');
class Calendar extends React.Component{
	constructor(props){
		super(props)
		this.state = {
			calendarAdded : false
		}
	}

	createCalendar = () =>{
		const city = this.props.city;
		const countryCode = this.props.countryCode;
		const calDiv = document.getElementById('calendar');
		if (calDiv.childElementCount){
	    	let innerDiv = document.getElementById('innerCalendar');
	    	innerDiv.remove();
	    }
		const div = document.createElement('div');
		//div.id='innerCalendar';
		function setAttributes(el, attrs) {
		  for(let key in attrs) {
		    el.setAttribute(key, attrs[key]);
		  }
		}
		setAttributes(div, {'w-type': 'calendar', 'w-tmapikey':'BTAzLLcXX3xXtooi8WdIktbOn1C3irib','w-googleapikey':"YOUR_GOOGLE_API_KEY",
							'w-keyword':'','w-theme':"calendar",'w-colorscheme':"light", 'w-width':"298", 'w-height':"400", 
							'w-size':"25", 'w-border':"1", 'w-borderradius':"4", 'w-postalcode':"",'w-radius':"25", 
							'w-period':"Thu Jan 09 2020 13:04:11 GMT+0200 (Israel Standard Time)",
							'w-periodweek':"week", 'w-layout':"vertical", 'w-classificationid':"", 'w-attractionid':"", 
							'w-promoterid':"", 'w-venueid':"", 'w-affiliateid':"", 'w-segmentid':"", 'w-proportion':"standart",
							'w-countrycode':countryCode, 'w-city':city, 'w-latlong':"" });
		// const script = document.createElement("script");
	 //    script.async = true;
	 //    script.src = "https://ticketmaster-api-staging.github.io/products-and-docs/widgets/calendar/1.0.0/lib/main-widget.js";
		calDiv.appendChild(div);
		// calDiv.appendChild(script);
	}

	componentDidMount() {
		if(this.props.city){
			this.createCalendar();
		}
	    console.log('got to calendar component did mount');
	    const calDiv = document.getElementById('calendar');
	    const script = document.createElement("script");
	    script.async = true;
	    script.src = "https://ticketmaster-api-staging.github.io/products-and-docs/widgets/calendar/1.0.0/lib/main-widget.js";;
		calDiv.appendChild(script);
		this.setState({calendarAdded : true});
  	}

	render(){
		if(this.props.isSignedIn){
			return(
				<div style={{display:'flex', justifyContent:'center'}}>
					<div>
						<div id='calendar' className='pa4' style={{display:'flex', justifyContent:'center'}}>
						</div>
						<button className='h2'>Add Event to my google calendar</button>
					</div>
					<button className='h3' onClick={this.SaveSearch}>Save This Search</button>
				</div> 
			);
		}else{
			return(
				<div id='calendar' className='pa4' style={{display:'flex', justifyContent:'center'}}>
				</div>
			);
		}
	}
		
}

export default Calendar;
